properties.query:
let PrimerUmbralWarning = 50 ;
let SegundoUmbralCritical = 150 ;
let Mango = view () {
Usage
| where TimeGenerated >= ago(24h) 
| where IsBillable == true
| summarize TotalVolumeGB = sum(Quantity) / 1000 by bin(StartTime, 1d), DataType
| summarize Media_consumo_total_GB = round(avg(TotalVolumeGB), 2) by DataType
| summarize Total_Daily_Consuption_Average_GBD = round(sum(Media_consumo_total_GB),2)
| extend alerta = iff(Total_Daily_Consuption_Average_GBD > PrimerUmbralWarning, strcat('Detecctada anomalia en recoleccion, superando umbral de advertencia ', PrimerUmbralWarning, " GBD"), iff(Total_Daily_Consuption_Average_GBD > SegundoUmbralCritical, strcat('Detecctada anomalia en recoleccion, superando umbral critico: ', SegundoUmbralCritical, ' GBD [Recoleccion Suspendida]'),strcat("Recoleccion dentro de parametros normales , la media actual diaria: ",Total_Daily_Consuption_Average_GBD, " GBD"))) 
};
Mango
| where Total_Daily_Consuption_Average_GBD > SegundoUmbralCritical
| project TenantDetection = "Mango", Total_Daily_Consuption_Average_GBD, alerta
